import React, { useState } from 'react';
import { Button, Checkbox, Form } from 'semantic-ui-react'
import { Menu } from 'semantic-ui-react'
import { Link } from 'react-router-dom';


export default   function InsertWriter() {
  return (<NewWriter />);
}

class NewWriter extends React.Component {
render() {


    const [writer, setWriier] = useState('');
    const [color, setColor] = useState('');
    const postData = () => {
        console.log(writer);
        console.log(color);
    }
    const { activeItem } = this.state


    handleItemClick = (e, { name }) => this.setState({ activeItem: name })
    return (
          <div className="col-xs-8 col-xs-offset-2 jumbotron">
            <div className="col-lg-12">
              <div className="table" >
                <span className="row">
                  <span className="column">
                    <Link to='/'>
                      <a>List Quote</a>
                    </Link>
                  </span>
                </span>  
              </div> 
              <Menu>
                <Menu.Item
                  name='editorials'
                  active={activeItem === 'editorials'}
                  onClick={this.handleItemClick}
                >
                    Editorials
                  </Menu.Item>
              </Menu> 
              <Form className="create-form">
                  <Form.Field>
                      <label>Writer</label>
                      <input placeholder='Victor Hugo Name' onChange={(e) => setWriter(e.target.value)}/>
                  </Form.Field>
                  <Form.Field>
                      <label>Color</label>
                      <input placeholder='#4285F4' onChange={(e) => setColor(e.target.value)}/>
                  </Form.Field>
                  <Button onClick={postData} type='submit'>Submit</Button>
              </Form>
          </div>
        </div>

    )
}
}
