                      <a class="item"> Refresh</a>
                </Menu.Item>

              </Menu> 
              <Form className="create-form" onSubmit={this.handleSubmit}>
                  <Form.Field>
                      <label>Writer</label>
                      <input placeholder='Victor Hugo Name' onChange={this.handleChange}/>
                  </Form.Field>
                  <Form.Field>
                      <label>Color</label>
                      <input placeholder='#4285F4' onChange={this.handleChange}/>
