import React, { useState } from 'react';
import { Button, Checkbox, Form } from 'semantic-ui-react'
import { Menu } from 'semantic-ui-react'
import { Link } from 'react-router-dom';


export default function InsertWriter() {
  return (<NewWriter />);
}

class NewWriter extends React.Component {


    constructor(props) {
      super(props);
      this.state = {
        writer: '',
        color: '',
      }

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }



  refresh() {
    window.location.reload();
  }


  handleChange(event) {
    this.setState({writer: event.target.writer});
    this.setState({color: event.target.color});
  }

  handleSubmit(event) {
    alert('Submitted: ' + this.state.writer +' Color :'+this.state.color);
    event.preventDefault();
  }


  render() {

    return (
          <div className="col-xs-8 col-xs-offset-2 jumbotron">
            <div className="col-lg-12">
              <div className="table" >
                <span className="row">
                  <span className="column">
                    <Link to='/'>
                      <a>List Quote</a>
                    </Link>
                  </span>
                </span>  
              </div> 

              <Form className="create-form" onSubmit={this.handleSubmit}>
                  <Form.Field>
                      <label>Writer</label>
                      <input placeholder='Victor Hugo Name' value={this.state.writer} onChange={this.handleChange}/>
                  </Form.Field>
                  <Form.Field>
                      <label>Color</label>
                      <input placeholder='#4285F4' value={this.state.color} onChange={this.handleChange}/>
                  </Form.Field>
                  <Button type='submit'>Submit</Button>
              </Form>
          </div>
        </div>

    )
}
}
