import React, { useState } from 'react';
import { Button, Checkbox, Form } from 'semantic-ui-react'
import { Menu } from 'semantic-ui-react'
import { Link } from 'react-router-dom';
import {useHistory} from 'react-router-dom';
import { withRouter } from "react-router-dom";




//export default function InsertWriter() {
//  return (<NewWriter />);
//}

export default withRouter(InsertWriter);

class InsertWriter extends React.Component {



    constructor(props) {
      super(props);
      this.state = {
        writer: '',
        color: '',
      }

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }



  refresh() {
    window.location.reload();
  }


  handleChange(event) {
//    this.setState({writer: event.target.writer});
//    this.setState({color: event.target.color});
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(event) {
 //   alert('Submitted: Writer :' + this.state.writer +' Color :'+this.state.color);
    window.$.get(`https://${process.env.REACT_APP_BACK_URL}/api/writers/insertwriter/${this.state.writer}/${encodeURIComponent(this.state.color)}`);
 //   let history = useHistory();
 //withRouter.history.push('/');
 const { history } = this.props;
 if(history) history.push('/');
  }

  redirectToHome = () => {
    const { history } = this.props;
    if(history) history.push('/');
   }

  render() {
    const { history } = this.props;
    return (

          <div className="col-xs-8 col-xs-offset-2 jumbotron">
            <div className="col-lg-12">
              <div className="table" >
                <span className="row">
                  <span className="column">
                    <Link to='/'>
                      <a>List Quote</a>
                    </Link>
                  </span>
                </span>  
              </div> 

              <Form className="create-form" onSubmit={this.handleSubmit}>
                  <Form.Field>
                      <label>Writer
                        <input name='writer' placeholder='Author Name' value={this.state.writer} onChange={this.handleChange}/>
                      </label>
                  </Form.Field>
                  <Form.Field>
                      <label>Color
                        <input name='color' placeholder='#4285F4' value={this.state.color} onChange={this.handleChange}/>
                      </label>
                  </Form.Field>

                      <Button type='submit'>Submit</Button>
              </Form>
          </div>
        </div>

    )
}
}
